  const {
    quoted,
    ValueIsBoolean,
    ValueIsNumber, ValueIsFiniteNumber, ValueIsNumberInRange,
    ValueIsInteger, ValueIsIntegerInRange,
    ValueIsString, ValueIsNonEmptyString, ValueIsStringMatching,
      ValueIsTextline,
    ValueIsPlainObject,
    ValueIsListSatisfying,
    ValueIsFunction,
    ValueIsOneOf,
    ValueIsURL,
    ValidatorForClassifier, acceptNil, rejectNil,
    allowNumber, allowCardinal,
    allowedString, expectString,
      allowNonEmptyString, expectNonEmptyString, expectedNonEmptyString,
      expectStringMatching, expectTextline,
    expectedObject, allowPlainObject, expectPlainObject,
    allowListSatisfying, expectListSatisfying,
    allowOneOf, expectOneOf,
    allowURL,
  } = global.get('JIL')

/**** common Code ****/

  class commonCode {
/**** throwError (Error object with HTTP status code) ****/

  static throwError (StatusCode, Message) {
    const decoratedError = new Error(Message)
      decoratedError['StatusCode'] = StatusCode
    throw decoratedError
  }

/**** newUUID - generates a Type 4 UUID ****/

  static newUUID () {
    let UUID = '', UUIDPart
    UUIDPart = Math.round(Math.random()*0xffffffff).toString(16)
      UUID += UUIDPart + '00000000'.slice(UUIDPart.length) + '-'
      UUIDPart = Math.round(Math.random()*0xffff).toString(16)
      UUID += UUIDPart + '0000'.slice(UUIDPart.length) + '-4'
      UUIDPart = Math.round(Math.random()*0xfff).toString(16)
      UUID += UUIDPart + '000'.slice(UUIDPart.length) + '-'
      UUIDPart = Math.round(Math.random()*0x3fff+0x8000).toString(16)
      UUID += UUIDPart + '-'
      UUIDPart = Math.round(Math.random()*0xffffffffffff).toString(16)
      UUID += UUIDPart + '000000000000'.slice(UUIDPart.length)
    return UUID.toLowerCase()
  }

/**** ProgressOf - auxiliary function for better readability ****/

  static ProgressOf (msg,Progress) {
    msg.Progress = Progress
    return msg
  }

/**** ValueIsChatMessage ****/

  static ValueIsChatMessage (Value) {
    return (
      ValueIsPlainObject(Value) &&
//    ValueIsOneOf(Value.role,['system','user','assistant']) &&
      ValueIsNonEmptyString(Value.role) &&
      ValueIsString(Value.content)
    )
  }

/**** ValueIsChatMessageList ****/

  static ValueIsChatMessageList (Value) {
    if (
      ValueIsListSatisfying(Value,ValueIsChatMessage) &&
      (Value.length > 0)
    ) {
      Value = Value.filter(
        (Message) => (['system','user','assistant'].indexOf(Message.role) >= 0)
      ) // keeps system/user/assistant messages which must have a specific order

      let i = 0, l = Value.length
      if (Value[0].role === 'system') { i++ }

      for (;i < l; i += 2) {
        if (Value[i].role !== 'user') { return false }
        if (i+1 === l) { break }

        if (Value[i+1].role !== 'assistant') { return false }
      }

      return true
    }
    return false
  }

/**** allow/expect[ed]ChatMessageList ****/

  static allowChatMessageList = ValidatorForClassifier(
    commonCode.ValueIsChatMessageList, acceptNil, 'chat message list'
  )
  static allowedChatMessageList = commonCode.allowChatMessageList

  static expectChatMessageList = ValidatorForClassifier(
    commonCode.ValueIsChatMessageList, rejectNil, 'chat message list'
  )
  static expectedChatMessageList = commonCode.expectChatMessageList

/**** ValueIsToolDescriptor ****/

  static ValueIsToolDescriptor (Value) {
    function ValueIsNumberList (Value) {
      return ValueIsListSatisfying(Value,ValueIsFiniteNumber)
    }

    return (
      ValueIsPlainObject(Value) &&
      ValueIsNonEmptyString(Value.Name)        &&
      ValueIsNonEmptyString(Value.Description) &&
      ValueIsListSatisfying(Value.InputDescriptions,ValueIsNonEmptyString) && (
        ValueIsNonEmptyString(Value.Script) ||
        ValueIsFunction(Value.Code)
      ) && (
        (Value.Embeddings == null) ||
        ValueIsListSatisfying(Value.Embeddings,ValueIsNumberList)
      )
    )
  }

/**** allow/expect[ed]ToolDescriptor ****/

  static allowToolDescriptor = ValidatorForClassifier(
    commonCode.ValueIsToolDescriptor, acceptNil, 'AI tool descriptor'
  )
  static allowedToolDescriptor = commonCode.allowToolDescriptor

  static expectToolDescriptor = ValidatorForClassifier(
    commonCode.ValueIsToolDescriptor, rejectNil, 'AI tool descriptor'
  )
  static expectedToolDescriptor = commonCode.expectToolDescriptor

/**** ValueIsToolStore ****/

  static ValueIsToolStore (Value) {
    if (! ValueIsPlainObject(Value)) { return false }

    for (let Key in Value) {
      if (Key === '') {
        if (
          (Value[Key] != null) && ! ValueIsNonEmptyString(Value[Key])
        ) { return false }
      } else {
        if (! ValueIsToolDescriptor(Value[Key]) ) { return false }
      }
    }

    return true
  }

/**** allow/expect[ed]ToolStore ****/

  static allowToolStore = ValidatorForClassifier(
    commonCode.ValueIsToolStore, acceptNil, 'AI tool store'
  )
  static allowedToolStore = commonCode.allowToolStore

  static expectToolStore = ValidatorForClassifier(
    commonCode.ValueIsToolStore, rejectNil, 'AI tool store'
  )
  static expectedToolStore = commonCode.expectToolStore

/**** acceptableBoolean ****/

  static acceptableBoolean (Value, Default) {
    return (ValueIsBoolean(Value) ? Value : Default)
  }

/**** acceptableNumber ****/

  static acceptableNumber (Value, Default) {
    return (ValueIsNumber(Value) ? Value : Default)
  }

/**** acceptableNumberInRange ****/

  static acceptableNumberInRange (
    Value, Default,
    minValue = -Infinity, maxValue = Infinity, withMin = false, withMax = false
  ) {
    return (ValueIsNumberInRange(Value,minValue,maxValue,withMin,withMax) ? Value : Default)
  }

/**** acceptableInteger ****/

  static acceptableInteger (Value, Default) {
    return (ValueIsInteger(Value) ? Value : Default)
  }

/**** acceptableIntegerInRange ****/

  static acceptableIntegerInRange (
    Value, Default, minValue = -Infinity, maxValue = Infinity
  ) {
    return (ValueIsIntegerInRange(Value,minValue,maxValue) ? Value : Default)
  }

/**** acceptableString ****/

  static acceptableString (Value, Default) {
    return (ValueIsString(Value) ? Value : Default)
  }

/**** acceptableNonEmptyString ****/

  static acceptableNonEmptyString (Value, Default = undefined) {
    return (ValueIsString(Value) && (Value.trim() !== '') ? Value : Default)
  }

/**** acceptableStringMatching ****/

  static acceptableStringMatching (Value, Default, Pattern) {
    return (ValueIsStringMatching(Value,Pattern) ? Value : Default)
  }

/**** acceptableURL ****/

  static acceptableURL (Value, Default) {
    return (ValueIsURL(Value) ? Value : Default)
  }

/**** acceptableModel ****/

  static acceptableModel (Value, Purpose) {
    if (! ValueIsTextline(Value)) { return undefined }

    const ModelInfo = knownModelSet[Value.toLowerCase()]
    return (
      (ModelInfo != null) && (ModelInfo.Purposes.indexOf(Purpose) >= 0)
    ) ? Value : undefined
  }

/**** assertInspectableFolder ****/

  static async assertInspectableFolder (FolderPath) {
    try {
      const FileInfo = await fs.promises.stat(FolderPath)
      if (! FileInfo.isDirectory()) throwError(
        500,quoted(FolderPath) + ' is not a directory'
      )
    } catch (Signal) {
      if (Signal.code === 'ENOENT') {
        throwError(500,quoted(FolderPath) + ' does not exist')
      } else {
        throwError(500,'internal error: ' + Signal)
      }
    }
  }

/**** assertReadableFile ****/

  static async assertReadableFile (FilePath) {
    try {
      const FileInfo = await fs.promises.stat(FilePath)
      if (! FileInfo.isFile()) throwError(
        500,quoted(FilePath) + ' is not a file'
      )
    } catch (Signal) {
      if (Signal.code === 'ENOENT') {
        throwError(500,quoted(FilePath) + ' does not exist')
      } else {
        throwError(500,'internal error: ' + Signal)
      }
    }

    try {
      await fs.promises.access(FilePath, fs.constants.R_OK)
    } catch (Signal) {
      throwError(500,quoted(FilePath) + ' can not be read')
    }
  }

/**** assertExecutableFile ****/

  static async assertExecutableFile (FilePath) {
    try {
      const FileInfo = await fs.promises.stat(FilePath)
      if (! FileInfo.isFile()) throwError(
        500,quoted(FilePath) + ' is not a file'
      )
    } catch (Signal) {
      if (Signal.code === 'ENOENT') {
        throwError(500,quoted(FilePath) + ' does not exist')
      } else {
        throwError(500,'internal error: ' + Signal)
      }
    }

    try {
      await fs.promises.access(FilePath, fs.constants.X_OK)
    } catch (Signal) {
      throwError(500,quoted(FilePath) + ' is not executable')
    }
  }

/**** knownModels ****/

  static supportedArchitectures = ['llama','stablelm','gptneox']
  static supportedPurposes      = ['text','phrasing','code','embeddings']

  static knownModelSet = Object.create(null)

  static knownModels () {
    return Object.keys(knownModelSet).map((Id) => knownModelSet[Id].Model)
  }

  static ModelIsKnown (Model) {
    expectNonEmptyString('searched model',Model)
    return (Model.trim().toLowerCase() in knownModelSet)
  }

/**** learnModel ****/

  static learnModel (Descriptor) {
    let {
      Model, ModelURL, Creator, CreatorURL,
      Architecture, Purposes,
      Templates, ContextLength, ReversePrompts,
      Documentation, License, LicenseURL,
    } = expectedObject('model descriptor', Descriptor)

    expectTextline('model name',Model)

    allowURL('model URL',ModelURL)

    allowNonEmptyString('model creator name',Creator)
    allowURL            ('model creator URL',CreatorURL)

    expectOneOf('model architecture',Architecture,supportedArchitectures)

    function ValueIsPurpose (Value) {
      return ValueIsOneOf(Value,supportedPurposes)
    }
    expectListSatisfying('intended model purposes',Purposes,ValueIsPurpose)

    function normalizedTemplates (Templates) {
      let { Prefix,System,User,Assistant,Suffix } = Templates
        Prefix    =     allowedString('model system message default',Prefix)    || ''
        System    =    allowedString('model system message template',System)    || '${Content}'
        User      =      allowedString('model user message template',User)      || '${Content}'
        Assistant = allowedString('model assistant message template',Assistant) || '${Content}'
        Suffix    =              allowedString('model prompt suffic',Suffix)    || ''
      return { Prefix,System,User,Assistant,Suffix }
    }
    allowPlainObject('message template set',Templates)
    Templates = normalizedTemplates(Templates || {})

    allowCardinal('model context length',ContextLength)
      if (ContextLength  == null) { ContextLength  = 4096 }
    allowListSatisfying('reverse prompt list',ReversePrompts, ValueIsNonEmptyString)
      if (ReversePrompts == null) { ReversePrompts = [] }

    allowURL('model documentation URL',Documentation)

    allowNonEmptyString('model license',License)
    allowURL       ('model license URL',LicenseURL)

    const normalizedDescriptor = {
      Model, ModelURL, Creator, CreatorURL,
      Architecture, Purposes,
      Templates, ContextLength, ReversePrompts,
      Documentation, License, LicenseURL,
    }
    knownModelSet[Model.toLowerCase()] = normalizedDescriptor
  }

/**** learnModels ****/

  static learnModels (DescriptorList) {
    expectListSatisfying('list of model descriptors',DescriptorList,ValueIsPlainObject)
    DescriptorList.forEach((Descriptor) => learnModel(Descriptor))
  }

/**** FileLooksLikeAModel ****/

  static FileLooksLikeAModel (FilePath) {
    const FileSuffix = path.extname(FilePath)
    return /[.](bin|ggml|gguf)/.test(FileSuffix)
  }

/**** ModelFromFileName ****/

  static ModelFromFileName (FilePath) {
    const FileName = path.basename(FilePath).replace(/[.](bin|ggml|gguf)$/,'')

    let ModelName = FileName
      .replace(/[-.]q\d_[\da-z](_[a-z])?/i,'')
      .replace(/[.]ggml[a-z0-9]*/i,'')
      .replace(/_q\d_[\da-z](_[a-z])?$/i,'')
    return ModelName
  }

/**** availableModels ****/

  static async availableModels () {
    try {
      const ModelFolder = effectiveConfiguration().ModelFolder
      await assertInspectableFolder(ModelFolder)

      const FileNameList  = await fs.promises.readdir(ModelFolder)
      const ModelNameList = FileNameList.filter(
        (FileName) => FileLooksLikeAModel(FileName)
      ).map(
        (FileName) => ModelFromFileName(FileName)
      )

      return ModelNameList
    } catch (Signal) {
      if (Signal.statusCode == null) {
        throwError(500,'Internal Error: ' + Signal)
      } else {
        throw Signal
      }
    }
  }

/**** InfoForModel ****/

  static InfoForModel (Model) {
    return knownModelSet[Model.toLowerCase()]
  }

/**** FileForModel ****/

  static async FileForModel (Model) {
    const ModelFolder = effectiveConfiguration().ModelFolder

    try {
      const FileNameList  = await fs.promises.readdir(ModelFolder)
      const ModelFileList = FileNameList.filter((FileName) =>
        FileLooksLikeAModel(FileName) &&
        FileName.toLowerCase().startsWith(Model.toLowerCase())
      )
      if (ModelFileList.length > 0) {
        const FilePath = path.join(ModelFolder,ModelFileList[0])
          await assertReadableFile(FilePath)
        return FilePath
      } else {
        throwError(500,'no file for model ' + quoted(Model))
      }
    } catch (Signal) {
      if (Signal.statusCode == null) {
        throwError(500,'Internal Error: ' + Signal)
      } else {
        throw Signal
      }
    }
  }

/**** configure ****/

  static async configure (msg, Context) {
    expectPlainObject('msg object',msg)
    allowOneOf ('Node-RED context',Context, [flow,global])

    if (Context == null) { Context = flow }

    let Configuration = Object.assign({
      ModelFolder:undefined, ExecutableFolder:undefined,
        ToolStoreFolder:undefined, RecipeStoreFolder:undefined,
      DefaultTextModel:undefined, DefaultPhrasingModel:undefined,
        DefaultCodeModel:undefined, DefaultEmbeddingsModel:undefined,
    }, Context.get('AI-Toolkit-Configuration') || {})

  /**** ModelFolder ****/

    let ModelFolder = acceptableNonEmptyString(msg.ModelFolder)
      if (ModelFolder != null) try {
        await assertInspectableFolder(ModelFolder)
      } catch (Signal) {
        node.warn(Signal.message)
        ModelFolder = undefined
      }
    if (ModelFolder != null) {
      Configuration.ModelFolder = ModelFolder
    }

  /**** ExecutableFolder ****/

    let ExecutableFolder = acceptableNonEmptyString(msg.ExecutableFolder)
      if (ExecutableFolder != null) try {
        await assertInspectableFolder(ExecutableFolder)
      } catch (Signal) {
        node.warn(Signal.message)
        ExecutableFolder = undefined
      }
    if (ExecutableFolder != null) {
      Configuration.ExecutableFolder = ExecutableFolder
    }

  /**** ToolStoreFolder ****/

    let ToolStoreFolder = acceptableNonEmptyString(msg.ToolStoreFolder)
      if (ToolStoreFolder != null) try {
        await assertInspectableFolder(ToolStoreFolder)
      } catch (Signal) {
        node.warn(Signal.message)
        ToolStoreFolder = undefined
      }
    if (ToolStoreFolder != null) {
      Configuration.ToolStoreFolder = ToolStoreFolder
    }

  /**** RecipeStoreFolder ****/

    let RecipeStoreFolder = acceptableNonEmptyString(msg.RecipeStoreFolder)
      if (RecipeStoreFolder != null) try {
        await assertInspectableFolder(RecipeStoreFolder)
      } catch (Signal) {
        node.warn(Signal.message)
        RecipeStoreFolder = undefined
      }
    if (RecipeStoreFolder != null) {
      Configuration.RecipeStoreFolder = RecipeStoreFolder
    }

  /**** DefaultTextModel ****/

    let DefaultTextModel = acceptableModel(msg.DefaultTextModel,'text')
    if (DefaultTextModel != null) {
      Configuration.DefaultTextModel = DefaultTextModel
    }

  /**** DefaultPhrasingModel ****/

    let DefaultPhrasingModel = acceptableModel(msg.DefaultPhrasingModel,'phrasing')
    if (DefaultPhrasingModel != null) {
      Configuration.DefaultPhrasingModel = DefaultPhrasingModel
    }

  /**** DefaultCodeModel ****/

    let DefaultCodeModel = acceptableModel(msg.DefaultCodeModel,'code')
    if (DefaultCodeModel != null) {
      Configuration.DefaultCodeModel = DefaultCodeModel
    }

  /**** DefaultEmbeddingsModel ****/

    let DefaultEmbeddingsModel = acceptableModel(msg.DefaultEmbeddingsModel,'embeddings')
    if (DefaultEmbeddingsModel != null) {
      Configuration.DefaultEmbeddingsModel = DefaultEmbeddingsModel
    }

  /**** Language Detection and Translation ****/

    let LanguageAPIKey        =     acceptableString(msg.LanguageAPIKey,'')
    let LanguageDetectorURL   =   acceptableURL(msg.LanguageDetectorURL,'https://libretranslate.com/detect')
    let LanguageTranslatorURL = acceptableURL(msg.LanguageTranslatorURL,'https://libretranslate.com/translate')

  /**** now configure potential "options" (DRY) ****/

    Object.assign(Configuration,await OptionsFrom(msg,{},Configuration))

  /**** that's it ****/

    Context.set('AI-Toolkit-Configuration',Configuration)
  }

/**** configureGlobally ****/

  static async configureGlobally (msg) {
    configure(msg,global)
  }

/**** effectiveConfiguration ****/

  static effectiveConfiguration () {
    const globalConfiguration = global.get('AI-Toolkit-Configuration')
    const localConfiguration  = flow.get('AI-Toolkit-Configuration')

    if (
      (globalConfiguration == null) &&
      (localConfiguration  == null)
    ) throwError(
      500, 'missing or empty AI Toolkit Configuration'
    )

    return Object.assign({},
      globalConfiguration || {},
      localConfiguration  || {}
    )                          // local configurations may overwrite global ones
  }

/**** OptionsFrom ****/
// "Options" may be function-specific, a "Configuration" is function-independent

  static async OptionsFrom (specificSettings, generalSettings, Configuration) {
    if (Configuration == null) {
      Configuration = effectiveConfiguration()
    }

    if (generalSettings  == null) { generalSettings  = {} }
    if (specificSettings == null) { specificSettings = {} }

  /**** construct complete set of options ****/

    let Options = Object.assign(
      {}, Configuration, generalSettings, specificSettings
    )

  /**** now validate all options and correct invalid settings ****/

    let Model = acceptableString(Options.Model, '')
    if (knownModelSet[Model.toLowerCase()] == null) {
      Model = Configuration.DefaultTextModel
    }
    let ModelInfo = InfoForModel(Model)

    let Threads          = acceptableIntegerInRange(Options.Threads,            4, 1,os.cpus().length)
    let Batches          = acceptableIntegerInRange(Options.Batches,            8, 1,100)
    let Seed             = acceptableIntegerInRange(Options.Seed,              -1, -1)
    let ContextLength    = acceptableIntegerInRange(Options.ContextLength,    512, 1,ModelInfo.ContextLength)
    let HeaderLength     = acceptableIntegerInRange(Options.HeaderLength,       0, 0,ContextLength)
    let PredictionLength = acceptableIntegerInRange(Options.PredictionLength, 128, 1,ContextLength)
    let Temperature      =  acceptableNumberInRange(Options.Temperature,      0.8, 0.0,1.0, true,true)
    let topK             = acceptableIntegerInRange(Options.topK,              40, 1,100)
    let topP             =  acceptableNumberInRange(Options.topP,             0.9, 0.1,1.0, true,true)
    let Grammar          =         acceptableString(Options.Grammar,           '').trim()

    return {
      Model, Threads, Batches,
