/**** CompletionOfText ****/

  static async CompletionOfText (Text, Options) {
    let Prompt = acceptableString(Text,'').trim()
    if (Prompt === '') throw new Error('missing or empty text')

    const Configuration  = effectiveConfiguration()
    let ExecutableFolder = Configuration.ExecutableFolder

    let {
      Model,
      Seed, Threads, Batches,
      ContextLength, HeaderLength, PredictionLength,
      Temperature, topK, topP,
      Grammar
    } = Options

    if (Model == null) { Model = Configuration.DefaultTextModel }

    let ModelFile = await FileForModel(Model)
    let ModelInfo = InfoForModel(Model)

    ContextLength    = Math.min(ContextLength,ModelInfo.ContextLength)
    PredictionLength = Math.min(PredictionLength,ContextLength)
    HeaderLength     = Math.min(HeaderLength,Math.max(0,ContextLength-PredictionLength))

    Prompt = Prompt.replace(/"/g, '\\"')

  /**** determine executable ****/

    let ExecutableFile = path.join(
      ExecutableFolder, ModelInfo.Architecture === 'gptneox' ? 'gpt-neox' : 'llama'
    )
    await assertExecutableFile(ExecutableFile)

  /**** combine all these settings into a command ****/

    let Command
    if (ModelInfo.Architecture === 'gptneox') {
      Command = (
        ExecutableFile + ' --model ' + ModelFile +
        ' --context ' + ContextLength +
        ' --n_predict ' + PredictionLength +
        ' --threads ' + Threads + ' --batch_size ' + Batches +
        (Seed < 0 ? '' : ' --seed ' + Seed) + ' --temp ' + Temperature +
        ' --top_k ' + topK + ' --top_p ' + topP +
        ' --prompt \'' + encoded(Prompt) + '\''
      )
    } else {
      Command = (
        ExecutableFile + ' --model ' + ModelFile + ' --mlock ' +
        ' --ctx_size ' + ContextLength + ' --keep ' + HeaderLength +
        ' --n_predict ' + PredictionLength +
        ' --threads ' + Threads + ' --batch_size ' + Batches +
        ' --seed ' + Seed + ' --temp ' + Temperature +
        ' --top_k ' + topK + ' --top_p ' + topP +
        ' --tfs 0.95 --mirostat 2'
      )

      ModelInfo.ReversePrompts.forEach(
        (ReversePrompt) => Command += ' --reverse-prompt ' + quoted(ReversePrompt)
      )

      Grammar = Grammar || ''
      Command += (
        (Grammar === '' ? '' : ' --grammar \'' + encoded(Grammar.replace(/\\/g,'\\\\')) + '\'') +
        ' --prompt \'' + encoded(Prompt) + '\''
      )
    }
node.warn('Command = ' + Command)

    const CommandOptions = {
      cwd:ExecutableFolder
    }

  /**** prepare response extraction from command output ****/

    let ResponseFrom
    if (ModelInfo.Architecture === 'gptneox') {
      ResponseFrom = function (Text) {
        let HeaderLength = Text.indexOf(
          '\n', Text.indexOf(Prompt) + Prompt.length
        )
        Text = Text.slice(HeaderLength + 1)

        let TrailerIndex = Text.indexOf('<|endoftext|>')
        if (TrailerIndex < 0) {
          TrailerIndex = Text.indexOf('\nmain: mem per token')
        }
        if (TrailerIndex >= 0) { Text = Text.slice(0, TrailerIndex) }

        return Text
      }
    } else {
      ResponseFrom = function (Text) {
        let HeaderLength = Text.indexOf('\n\n\n')
        Text = Text.slice(HeaderLength + 1)

        let TrailerIndex = Text.indexOf('<|endoftext|>')
        if (TrailerIndex < 0) {
          TrailerIndex = Text.indexOf('\nllama_print_timings')
        }
        if (TrailerIndex >= 0) { Text = Text.slice(0, TrailerIndex) }

        return Text
      }
    }

  /**** now infer a response from the given prompt ****/

    try {
      let stdout = '', stderr = ''
      const ShellProcess = child_process.exec(Command, CommandOptions)
        for await (const Chunk of ShellProcess.stdout) { stdout += Chunk }
        for await (const Chunk of ShellProcess.stderr) { stderr += Chunk }
      const ExitCode = (await new Promise((resolve) => {
        ShellProcess.on('close', resolve)
      })) || 0

      if (ExitCode !== 0) {
// @ts-ignore 2365 (ExitCode is actually a number)
        throwError(500 + ExitCode,stderr || 'text completion failed')
      } else {
        return (stdout.trim() === '' ? '' : ResponseFrom(stdout))
      }
    } catch (Signal) {
      if (Signal.StatusCode == null) {
        throwError(500,'Internal Error: ' + Signal)
      } else {
        throw Signal
      }
    }

  /**** encoded ****/

    function encoded(Text) {
      return Text.replace(/'/g, "'\"'\"'")
    }
  }

