/**** PromptFromChatMessages ****/

  static PromptFromChatMessages (ChatMessageList,Model) {
    expectChatMessageList('list of chat messages',ChatMessageList)

    let ModelInfo = InfoForModel(Model)
    let Templates = ModelInfo.Templates

    let Prompt = ''
      if ((ChatMessageList[0] != null) && (ChatMessageList[0].role !== 'system')) {
        Prompt = Templates.Prefix || ''
      }

      ChatMessageList.forEach((ChatMessage) => {
        switch (ChatMessage.role) {
          case 'system':
            Prompt += Templates.System.replace('${Content}',ChatMessage.content)
            break
          case 'user':
            Prompt += Templates.User.replace('${Content}',ChatMessage.content)
            break
          case 'assistant':
            Prompt += Templates.Assistant.replace('${Content}',ChatMessage.content)
            break
        }
      })

      Prompt += Templates.Suffix || ''
    return Prompt
  }

