/**** ListFromText ****/

  static async ListFromText (Request, Text, Options) {
    if (Request.trim() === '') throwError(500, 'missing or empty request')
    if (Text.trim()    === '') { return '' }

    let Configuration = effectiveConfiguration()
    let {
      Model,
      Seed, Threads, Batches,
      ContextLength, HeaderLength, PredictionLength,
      Temperature, topK, topP,
    } = Object.assign(
      { Model:Configuration.DefaultTextModel }, Configuration, Options
    )

    let ChatMessages = [{
      role:'system', content:`
Consider the following text:

"${Text}"

Now analyze the text shown above and extract the information requested below.
If the text does not contain this information, provide no output. Otherwise,
present the information as a list with each item on a separate line starting
with "- ".

The requested information is:
      `.trim() + ' '
    },{
      role:'user', content:Request
    }]

    let List = (await CompletionOfChat(ChatMessages,{
      Model,
      Seed, Threads, Batches,
      ContextLength, HeaderLength, PredictionLength,
      Temperature, topK, topP,
    })).split('\n')

    const NumberedListPattern = /^\s*\d+[.]?\s*/
    switch (true) {
      case List.some((Element) => /^\s*[-]\s*/.test(Element)):
        return List
          .map((Element) => Element.replace(/^\s*[-]\s*/,''))
          .filter((Line) => Line.trim() !== '')
      case List.some((Element) => /^\s*[*]\s*/.test(Element)):
        return List
          .map((Element) => Element.replace(/^\s*[*]\s*/,''))
          .filter((Line) => Line.trim() !== '')
      case List.some((Element) => NumberedListPattern.test(Element)):
        return List
          .map((Element) => Element.replace(NumberedListPattern,''))
          .filter((Line) => Line.trim() !== '')
      default:
        return List.filter((Line) => Line.trim() !== '')
    }

    return []
  }

