      role:'system', content:`
Generate a sequence of JavaScript statements that address a specified problem.
The statements should take data from an input array and store the final result
in an output variable. Please ensure that the output variable contains the
solution to the problem.
      `.trim() + ' '
    },{
      role:'user', content:Problem
    }]

    let Solution = await CompletionOfChat(ChatMessages,{
      Model,
      Seed, Threads, Batches,
      ContextLength, HeaderLength, PredictionLength,
      Temperature, topK, topP,
    })

    if (Solution.indexOf('```') >= 0) {
      Solution = Solution
        .replace(/^((?!```)[\s\S])*```[^\n]*[\n]/,'')
        .replace(/```[\s\S]*$/,'')
    }

    let LogIndex = Solution.lastIndexOf('console.log')
    if (LogIndex >= 0) {
      Solution = (
        Solution.slice(0,LogIndex) + 'return ' + Solution.slice(LogIndex+11)
      )
    }

    return Solution
  }

