  static async ResultsOfSearch (Prompt) {
    if (Prompt.trim() === '') { return '' }

    let Response = await axios.get(
      `https://www.startpage.com/do/search?query=${encodeURIComponent(Prompt)}&lui=english`
    )

  /**** prepare response extraction from the received web page ****/

    function ResultsFrom (Response) {
      const $ = cheerio.load(Response.data)
      const Results = []
        $('.w-gl__result').each((index, element) => {
          const Title       = $(element).find('.w-gl__result-title').text().trim()
          const URL         = $(element).find('.w-gl__result-title').attr('href')
          const Description = $(element).find('.w-gl__description').text().trim()
// @ts-ignore 2345 (calm the TypeScript validator)
          Results.push({ Date:Date.now(), Title, URL, Description })
        })
      return Results
    }

  /**** now analyze the received web page ****/

    switch (Response.status) {
      case 200: return ResultsFrom(Response)
      case 204: return []
      default:  throwError(Response.status, Response.statusText)
    }
  }

/**** TextAtURL ****/

  static async TextAtURL (URL, Options) {
    if (URL.trim() === '') { return '' }

    let Response = await axios.get(URL)

  /**** prepare response extraction from the received data ****/

    function ResultsFrom (Response) {
      const ContentType = Response.headers['content-type']
      switch (true) {
        case ContentType.includes('text/html'):
          let $ = cheerio.load(Response.data)
          $('body').find(
            'style,script,nav,form,meter,progress,input,textarea,select,button,label'
          ).remove()

          return $('body').find(
            'h1,h2,h3,h4,h5,h6,article,p,li,th,td,caption,blockquote'
          ).map((Index,Element) => $(Element).text()).get().join(' ')
        case ContentType.includes('text/'):
          return Response.data
        default:
          return ''
      }
    }

  /**** now analyze the received data ****/

    switch (Response.status) {
      case 200: return ResultsFrom(Response)
      case 204: return []
      default:  throwError(Response.status, Response.statusText)
    }
  }

/**** LanguageOfText ****/

  static async LanguageOfText (Text, Options) {
    if (Text.trim() === '') { return '' }

    const {
      LanguageDetectorURL, LanguageAPIKey
    } = Options

    const RequestBody = { q:Text }
    if ((LanguageAPIKey || '').trim() !== '') {
      RequestBody['api_key'] = LanguageAPIKey
    }

    let Response = await axios.post(
      LanguageDetectorURL || 'https://libretranslate.com/detect',
      RequestBody
    )

    switch (Response.status) {
      case 200: let { language:Language,confidence:Confidence } = Response.data[0]
                return { Language, Confidence }
      case 204: return ''
      case 400:
      case 403:
      case 429:
      case 500: throwError(Response.status, Response.data.error)
      default:  throwError(Response.status, Response.statusText)
    }
  }

/**** TranslationOfText ****/

  static async TranslationOfText (Text, Options, originalLanguage = 'auto') {
    if (Text.trim() === '') { return '' }

    const {
      LanguageTranslatorURL, LanguageAPIKey
    } = Options

    const RequestBody = {
      q:Text,
      source:originalLanguage, target:'en', format:'text',
    }
    if ((LanguageAPIKey || '').trim() !== '') {
      RequestBody['api_key'] = LanguageAPIKey
    }

    let Response = await axios.post(
      LanguageTranslatorURL || 'https://libretranslate.com/translate',
      RequestBody
    )

    switch (Response.status) {
      case 200: return Response.data.translatedText
      case 204: return ''
      case 400:
      case 403:
      case 429:
      case 500: throwError(Response.status, Response.data.error)
      default:  throwError(Response.status, Response.statusText)
    }
  }

