/**** CosineSimilarity ****/

  static CosineSimilarity (EmbeddingsA,EmbeddingsB) {
    expectListSatisfying('1st embeddings vector',EmbeddingsA, ValueIsNumber)
    expectListSatisfying('2nd embeddings vector',EmbeddingsB, ValueIsNumber)

    if (EmbeddingsA.length !== EmbeddingsB.length) {
      throwError(500,'embeddings vectors must always have the same length')
    }

    let ProductSum = 0, SquareASum = 0, SquareBSum = 0
    for (let i = 0, l = EmbeddingsA.length; i < l; i++) {
      const A = EmbeddingsA[i], B = EmbeddingsB[i]
      ProductSum += A*B; SquareASum += A*A; SquareBSum += B*B
    }
    return ProductSum / Math.sqrt(SquareASum*SquareBSum)
  }

