/**** loadToolStoreFromFile - loads Tool Store from file ****/

  static async loadToolStoreFromFile (FilePath) {
    expectString('tool store file path',FilePath)

    resetToolStore()                                   // yet without embeddings
    if (FilePath.trim() === '') { return }

    const { ToolStoreFolder } = effectiveConfiguration()

    const ToolStoreFile = path.join(ToolStoreFolder,FilePath)
    try {
      const FileContents = (await fs.promises.readFile(ToolStoreFile, 'utf8')).trim()
      if (FileContents === '') { return }

      let ToolSet = JSON.parse(FileContents)
      expectToolStore('AI tool store',ToolSet)

      for (let ToolName in ToolSet) {
        let Tool = ToolSet[ToolName]
        if (Tool.Code == null) {
          Tool.Code = compiledTool(Tool)
        }
      }

      let ToolStore = global.get('AI-Toolkit-ToolStore')
        if (ToolSet[''] != null) {
          const EmbeddingsModel = ToolSet['']
          if (EmbeddingsModel !== ToolStore['']) {
            for (let ToolName in ToolStore) {
              if (! (ToolName in ToolSet)) {       // entry won't be overwritten
                await calculateEmbeddingsForTool(ToolStore[ToolName],EmbeddingsModel)
              }
            }
          }

          for (let ToolName in ToolSet) {
            if (ToolName === '') { continue }
            await calculateEmbeddingsForTool(ToolSet[ToolName],EmbeddingsModel)
          }
        }
        Object.assign(ToolStore,ToolSet)
      global.set('AI-Toolkit-ToolStore',ToolStore)
    } catch (Signal) {
      if (Signal.code === 'ENOENT') {
        return
      } else {
        throwError(500,'internal error: ' + Signal)
      }
    }
  }

/**** saveToolStoreToFile - saves Tool Store to file ****/

  static async saveToolStoreToFile (FilePath) {
    expectNonEmptyString('tool store file path',FilePath)
    if (FilePath.trim() === '') { return }

    const { ToolStoreFolder } = effectiveConfiguration()

    const ToolStoreFile = path.join(ToolStoreFolder,FilePath)
    try {
      const ToolStore = {...global.get('AI-Toolkit-ToolStore')}
        for (let ToolName in ToolStore) {
          if (ToolName === '') { continue }

          let Tool = ToolStore[ToolName]
          if (Tool.Script == null) {
            Tool.Script = Tool.Code.toString()
              .replace(/^[^(]*[(][^)]*[)][^\n]*[\n]/,'')
              .replace(/[}]\s*$/,'')
            delete Tool.Code
          }
        }

      await fs.promises.writeFile(
        ToolStoreFile, JSON.stringify(ToolStore, null, 2), 'utf8'
      )
    } catch (Signal) {
      throwError(500,'internal error: ' + Signal)
    }
  }

