/**** storedToolForTask ****/

  static async storedToolForTask (Task, Threshold) {
    expectNonEmptyString  ('tool task',Task)
    allowNumber('similarity threshold',Threshold)
      if (Threshold == null) { Threshold = 0 }

    const ToolStore = global.get('AI-Toolkit-ToolStore')

    let EmbeddingsModel = ToolStore['']
      if (EmbeddingsModel == null) {
        EmbeddingsModel = effectiveConfiguration()['DefaultEmbeddingsModel']
        expectNonEmptyString('tool store embeddings model',EmbeddingsModel)

        await calculateToolStoreEmbeddingsUsingModel(EmbeddingsModel)
      }

    let TaskEmbeddings = await calculateEmbeddingsUsingModel(
      Task, EmbeddingsModel
    )

    let bestToolName, bestSimilarity = -1
    for (let ToolName in ToolStore) {
      if (ToolName === '') { continue }

      let Similarity = CosineSimilarity(TaskEmbeddings,ToolStore[ToolName].Embeddings)
      if (Similarity < Threshold) { continue }

      if (Similarity > bestSimilarity) {
        bestToolName   = ToolName
        bestSimilarity = Similarity
      }
    }

    return bestToolName
  }

/**** storeTool ****/

  static async storeTool (Tool) {
    expectToolDescriptor('tool',Tool)

    const ToolStore = global.get('AI-Toolkit-ToolStore')
    ToolStore[Tool.Name] = {
      ...Tool, InputDescriptions:[...Tool.InputDescriptions]
    }

    if (ToolStore[Tool.Name].Code == null) {
      ToolStore[Tool.Name].Code = compiledTool(ToolStore[Tool.Name])
    }

    if ((ToolStore[''] != null) && (Tool.Embeddings == null)) {
      await calculateEmbeddingsForTool(ToolStore[Tool.Name],ToolStore[''])
    }
  }

